import React, { useState } from "react";
import { useCookies } from "react-cookie";
import { Link } from "react-router-dom";
import axios from "axios";
import "./login.css";
import { hasSelectionSupport } from "@testing-library/user-event/dist/utils";

export const Login: React.FC = () => {
  const [userName, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [Token, setToken]: any = useState("");
  const [user, setUser]: any = useState("");
  const [cookies, setCookie, removeCookie] = useCookies(["name"]);
  const [status, setStatus]: any = useState("");
  const [message, setMassage]: any = useState("");

  const urlLogin = "http://localhost:8000/rest-auth/login/";
  const urlUser = "http://localhost:8000/rest-auth/user/";

  function syncDelay(milliseconds: number) {
    var start = new Date().getTime();
    var end = 0;
    while (end - start < milliseconds) {
      end = new Date().getTime();
    }
  }

  const handleChange = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setName(e.target.value);
  };

  const handleEmail = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setEmail(e.target.value);
  };

  const handlePassword = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setPassword(e.target.value);
  };

  const logout = () => {
    document.cookie = "name=;expires=0;";
    window.location.reload();
  };

  const postData = () => {
    axios
      .post(urlLogin, {
        username: userName,
        email: email,
        password: password,
      })
      .then(res => {
        console.log(res);
        setToken(res.data.key);
        setCookie("name", Token);
        syncDelay(100);
        setStatus(res.status);
        console.log(res.status);
        console.log(Token);
        axios
          .get(urlUser, {
            headers: {
              Authorization: "Token " + Token,
            },
          })
          .then(res => {
            console.log(res.data.username);
            setUser(res.data.username);
            console.log(user);
          });
      })
      .catch(error => {
        console.log(error);
      })
      .finally(function () {
        console.log(status);
        if (status == 200) {
          setMassage("ログインに成功しました。");
        } else if (status == 401) {
          setMassage("サーバーエラーのためもう一度ログインを押してください。");
        } else {
          setMassage("ログインに失敗しました。入力情報を確認してください。");
        }
      });
  };

  return (
    <div className="container">
      &nbsp;
      <Link to="/" className="link">
        掲示板に戻る
      </Link>
      <p className="user">
        <p className="text">ユーザー名：{user}</p>
      </p>
      <p className="error">{message}</p>
      <p className="text">
        ユーザー名、パスワードはどちらか片方でログインできます。
      </p>
      <p className="input">ユーザー名</p>
      <input
        type="text"
        value={userName}
        onChange={handleChange}
        className="form-control"
      />
      <p className="input">メールアドレス</p>
      <input
        type="text"
        value={email}
        onChange={handleEmail}
        className="form-control"
      />
      <p className="input">パスワード</p>
      <input
        type="text"
        value={password}
        onChange={handlePassword}
        className="form-control"
      />
      <div className="button-space">
        <input className=".button-bg {
  text-align: center;
  box-sizing: border-box;
  /* 中央揃え */
  display: block;
  margin: 0 auto;
  /* 幅の指定 */
  width: 100%;
  max-width: 200px;
  padding: 10px;
  /* 色の指定 */
  color: #ff9300; /* 文字色 */
  border: 2px solid #ff9300; /* 枠線 */
  font-weight: bold;
  position: relative;
  overflow: hidden;
}
.button-bg::after {
  content: '';
  display: block;
  width: 100%;
  height: 100%;
  background: #ff9300; /* 背景色 */
  position: absolute;
  top: 0;
  left: 0;
  z-index: -1;
  transition: transform 0.2s;
}
.button-bg.from-left::after {
  transform: translate(-100%);
}
.button-bg.from-right::after {
  transform: translate(200%);
}
.button-bg.from-top::after {
  transform: translate(0, -100%);
}
.button-bg.from-bottom::after {
  transform: translate(0, 200%);
}
.button-bg:hover {
  color: #fff;
}
.button-bg:hover::after {
  transform: translate(0);
}
.button-bg:active, .button-bg:focus {
  top: 2px;
}" type="button" value="ログイン" onClick={postData} />
      </div>
      <div className="button-space">
        <input type="button" value="ログアウト" onClick={logout} />
      </div>
      &nbsp;
    </div>
  );
};
