    target: { value: React.SetStateAction<string> };
  }) => {
    setName(e.target.value);
  };

  const handleEmail = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setEmail(e.target.value);
  };

  const handlePassword = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setPassword(e.target.value);
  };

  const logout = () => {
    removeCookie("name");
  };

  const postData = () => {
    for (let i = 0; i < 5; i++) {
    axios
      .post(urlLogin, {
        username: userName,
        email: email,
        password: password,
      })
      .then(res => {
        console.log(res);
